import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import styled from 'styled-components';
import bubbleSort from './sort_algorithm.js'

class Line extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			value:this.props.value,
			isHover:false,
			style:{
				left:this.props.position,
				borderColor:this.props.color,
				top: 600 - parseInt(this.props.value),
			}
		}

		this.handleMouseOver = this.handleMouseOver.bind(this);
		this.handleMouseOut = this.handleMouseOut.bind(this);
	}


	handleMouseOver(){
		this.setState({
			isHover:true,
		})
		const obj = document.getElementById('item_value')
		obj.innerHTML = this.props.value;
		this.setState({style:{...this.state.style, borderColor:"#2894FF"}});
	}

	handleMouseOut(){
		this.setState({
			isHover:false,
		})
		const obj = document.getElementById('item_value')
		obj.innerHTML = "";
		this.setState({style:{...this.state.style, borderColor:'#FFD2D2'}});
	}
	render(){
		return (
			<div className='vl' style={this.state.style} onMouseOver={this.handleMouseOver} onMouseOut={this.handleMouseOut}></div>
		)
	}	



}

class SortVisualizer extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			lines : null,
			history : [Array(20).fill(null)],
			isSetUp:false,
			counter:0,
			currentStep:0,
		}
		document.body.style = 'background-color : #4F4F4F';
	}

	randomInput(){
		var input = [Array.from({length:20}, () => Math.floor(Math.random() * 400))];
		var history = [Array(20).fill(null)].concat(input);
		var counter = this.state.counter;
		this.setState({history:history, counter:counter+1, currentStep:1});
	}

	visualize(){
		// const history = bubbleSort(this);
		// this.setState({history:history});
		// this.setState({currentStep:10});

	}

	render(){
		const input = this.state.history[this.state.currentStep];

		const lines = input.map((value, index) => {
			if(this.state.currentStep != 0){
				let percentage = 100 / (input.length + 1) * index;
				percentage = percentage.toString() + "%";
				let cnt = this.state.counter;
				// let key = cnt.toString() + '_' + index.toString();

				return (
						<Line key={index} position={percentage} color='#FFD2D2' value={value}/>
					);	
			}else{
				return null;
			}	
		});

		return (
			<div>
				<nav className="bg-secondary navbar navbar-expand-lg p-4">
					<div className="collapse navbar-collapse">
							<ul className="navbar-nav">
									<li className="nav-item mx-4 pt-2">
										<select>
											<option className="nav-link text-white" value="Bubble_Sort">Bubble Sort</option>
											<option className="nav-link text-white" value="Insertion_Sort">Insertion Sort</option>
											<option className="nav-link text-white" value="Quick_Sort">Quick Sort</option>
											<option className="nav-link text-white" value="Merge_Sort">Merge Sort</option>
										</select>
									</li>
									<li className="nav-item">
										<label>Input : </label>
										<button onClick={() => this.randomInput()}>Randomize Input</button>
									</li>
									<li className="nav-item">
										<button onClick={() => this.visualize()}>Visualize</button>
									</li>
							</ul>

					</div>
				</nav>

				<div className="boundary">
					<label>Selection Item Value : <label id='item_value' style={{color:'White'}}></label></label>
					<li className="nav-link" >
						{lines}
					</li>
				</div>
			</div>
		);
	}

}


ReactDOM.render(
	<SortVisualizer />,
	document.getElementById('root'),
);
