import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';


class Line extends React.Component {

	render(){

		return (
			<div className='vl' style={{left:this.props.position}}></div>
		)
	}	

}

class SortVisualizer extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			input_array : Array(0)
		}
	}




	render(){
		var input_text = document.getElementById('input').value;
		console.log(input_text)

		const input = this.state.input_array;
		const lines = input.map((value) => {
			const percentage = value.toString() + "%"
			return (
				<Line position={percentage}/>
			);
		});


		return (
			<div>
				<nav className="bg-secondary navbar navbar-expand-lg p-4">
					<div className="collapse navbar-collapse">
							<ul className="navbar-nav">
									<li className="nav-item mx-4 pt-2">
										<select>
											<option className="nav-link text-white" value="Bubble_Sort">Bubble Sort</option>
											<option className="nav-link text-white" value="Insertion_Sort">Insertion Sort</option>
											<option className="nav-link text-white" value="Quick_Sort">Quick Sort</option>
											<option className="nav-link text-white" value="Merge_Sort">Merge Sort</option>
										</select>
									</li>
									<li className="nav-item">
										<label>Input : </label>
										<textarea id='input' onChange={()=>{
											const {value} = event.target;
											console.log()
										}}></textarea>
									</li>
							</ul>
					</div>
				</nav>

				<div className="boundary">
					<li className="nav-link">
						{lines}
					</li>
				</div>
			</div>
		);
	}

}


ReactDOM.render(
	<SortVisualizer />,
	document.getElementById('root')
);
