import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import styled from 'styled-components';
import bubbleSort from './sort_algorithm.js'

class Line extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			value:this.props.value,
			isHover:false,
			style:{
				left:this.props.position,
				borderColor:this.props.color,
				top: 600 - parseInt(this.props.value),
			}
		}

		this.handleMouseOver = this.handleMouseOver.bind(this);
		this.handleMouseOut = this.handleMouseOut.bind(this);
	}


	handleMouseOver(){
		this.setState({
			isHover:true,
		})
		const obj = document.getElementById('item_value')
		obj.innerHTML = this.props.value;
		this.setState({style:{...this.state.style, borderColor:"#2894FF"}});
	}

	handleMouseOut(){
		this.setState({
			isHover:false,
		})
		const obj = document.getElementById('item_value')
		obj.innerHTML = "";
		this.setState({style:{...this.state.style, borderColor:'#FFD2D2'}});
	}
	render(){
		return (
			<div className='vl' style={this.state.style} onMouseOver={this.handleMouseOver} onMouseOut={this.handleMouseOut}></div>
		)
	}	



}

