import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import styled from 'styled-components';


class Line extends React.Component {

	render(){
		const animate = {
			animationName:this.props.animationName,
			animationDuration:"2s",
			animationFillMode:"forwards",
		}
		console.log(animate);
		return (

			<div className='vl' style={{left:this.props.position, borderColor:this.props.color, animationName:animate.animationName, }} ></div>
		)
	}	

}

class SortVisualizer extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			input_array : Array(0),
			lines : null,
		}
	}

	readInput(){

		const text = document.getElementById('input').value;
		if(text.length === 0){
			return;
		}

		const text_splited = text.split(',');

		const int_input = text_splited.map((value)=> {return parseInt(value);});
		this.setState({input_array:int_input});

		const lines = int_input.map((value, index) => {
			// const percentage = value.toString() + "px";
			let percentage = 100 / (int_input.length + 1) * index;
			percentage = percentage.toString() + "%";
			let h = 300 - parseInt(value / 2.5);

			let animationName = 'grow' + value.toString()

			let keyFrame = `@keyframes ${animationName}{
				0% {
				    top: 400px;
				    bottom: 100px;
				  }
				100%{
				    top: ${h}px;
				    bottom: 100px;
				  }
			}`;
			console.log(keyFrame);


			let styleSheet = document.styleSheets[0];
			styleSheet.insertRule(keyFrame, styleSheet.cssRules.length);

			return (
				<Line position={percentage} color='Red' key={index} animationName={animationName}/>
			);
		});

		this.setState({lines:lines});
	}







	render(){

		const lines = this.state.lines;


		return (
			<div>
				<nav className="bg-secondary navbar navbar-expand-lg p-4">
					<div className="collapse navbar-collapse">
							<ul className="navbar-nav">
									<li className="nav-item mx-4 pt-2">
										<select>
											<option className="nav-link text-white" value="Bubble_Sort">Bubble Sort</option>
											<option className="nav-link text-white" value="Insertion_Sort">Insertion Sort</option>
											<option className="nav-link text-white" value="Quick_Sort">Quick Sort</option>
											<option className="nav-link text-white" value="Merge_Sort">Merge Sort</option>
										</select>
									</li>
									<li className="nav-item">
										<label>Input : </label>
										<textarea id='input'></textarea>
									</li>
									<li className="nav-item">
										<button onClick={() => this.readInput()}>Visualize</button>
									</li>
							</ul>
					</div>
				</nav>

				<div className="boundary">
					<li className="nav-link">
						{lines}
					</li>
				</div>
			</div>
		);
	}

}


ReactDOM.render(
	<SortVisualizer />,
	document.getElementById('root')
);
