const bubbleSort = function(parent){
	var ary = parent.state.input_array;
	var history = [];
	for(var i = 0; i < ary.length; i++){
		for(var j = 0 ; j < ary.length - i - 1; j++){
			if(ary[j] > ary[j+1]){
				var tmp = ary[j+1];
				ary[j+1] = ary[j];
				ary[j] = tmp;
				// parent.setState({input_array:ary});
				// parent.lineUpdate();
				history = history.concat([ary.slice()]);
				// console.log(history);
			}
		}
	}
	console.log(history);
}



export default bubbleSort
