import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';


class Line extends React.Component {

	render(){

		return (
			<div class='vl' style={{left:this.props.position}}></div>
		)
	}	

}

class SortVisualizer extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			input_array : [0, 99]
		}
	}

	render(){

		const input = this.state.input_array;
		const lines = input.map((value) => {
			const percentage = value.toString() + "%"
			return (
				<Line position={percentage}/>
			);
		})

		return (
			<div>
				<nav className="bg-secondary navbar navbar-expand-lg p-4">
					<div className="collapse navbar-collapse">
							<ul className="navbar-nav">
									<li className="nav-item mx-4 pt-2">
										<select>
											<option className="nav-link text-white" value="Bubble_Sort">Bubble Sort</option>
											<option className="nav-link text-white" value="Insertion_Sort">Insertion Sort</option>
											<option className="nav-link text-white" value="Quick_Sort">Quick Sort</option>
											<option className="nav-link text-white" value="Merge_Sort">Merge Sort</option>
										</select>
									</li>
									<li className="nav-item mx-4"><p className="nav-link text-white">Home2</p></li>
									<li className="nav-item mx-4"><p className="nav-link text-white">Home3</p></li>
									<li className="nav-item mx-4"><p className="nav-link text-white">Home4</p></li>
									<li className="nav-item mx-4"><p className="nav-link text-white">Home5</p></li>
							</ul>
					</div>
				</nav>
				<li className="nav-link">
					{lines}
				</li>

			</div>
		);
	}

}


ReactDOM.render(
	<SortVisualizer />,
	document.getElementById('root')
);
