const bubbleSort = function(parent){
	var ary = parent.state.input_array;

	for(var i = 0; i < ary.length; i++){
		for(var j = 0 ; j < ary.length - i - 1; j++){
			if(ary[j] > ary[j+1]){
				var tmp = ary[j+1];
				ary[j+1] = ary[j];
				ary[j] = tmp;
				// parent.setState({input_array:ary});
				// parent.lineUpdate();

				const org_his = parent.state.history;
				const new_his = ary.slice();
				parent.setState({
					history: org_his.concat([{
						array:new_his,
				}]),
				}, );
				console.log(parent.state.history);
			}
		}
	}

}



export default bubbleSort
