import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import styled from 'styled-components';


class Line extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			value:this.props.value,
			isHover:false,
		}

		this.handleMouseOver = this.handleMouseOver.bind(this);
		this.handleMouseOut = this.handleMouseOut.bind(this);
	}



	handleMouseOver(){
		this.setState({
			isHover:true,
		})
	}

	handleMouseOut(){
		this.setState({
			isHover:false,
		})
	}



	render(){

		const style = {
			left:this.props.position,
			borderColor:this.props.color,
			animationName:this.props.animationName,
			animationDuration:"2s",
			animationFillMode:"forwards",
		}

		return (
			<div className='vl' style={style} ></div>
		)
	}	

}

class SortVisualizer extends React.Component{

	constructor(props){
		super(props);
		this.state = {
			input_array : Array(0),
			lines : null,
		}
	}


	randomInput(){

		const input = Array.from({length:100}, () => Math.floor(Math.random() * 400))
		this.setState({input_array : input});

		const lines = input.map((value, index) => {
			// const percentage = value.toString() + "px";
			let percentage = 100 / (input.length + 1) * index;
			percentage = percentage.toString() + "%";
			let h = 600 - parseInt(value);

			let animationName = 'grow' + value.toString()

			let keyFrame = `@keyframes ${animationName}{
				0% {
				    top: 500px;
				    bottom: 100px;
				  }
				100%{
				    top: ${h}px;
				    bottom: 100px;
				  }
			}`;
			console.log(keyFrame);


			let styleSheet = document.styleSheets[0];
			styleSheet.insertRule(keyFrame, styleSheet.cssRules.length);

			return (
				<Line position={percentage} color='Red' key={index} animationName={animationName} value={value}/>
			);
		});

		this.setState({lines:lines});
	}


	visualize(){
		return;
	}




	render(){




		return (
			<div>
				<nav className="bg-secondary navbar navbar-expand-lg p-4">
					<div className="collapse navbar-collapse">
							<ul className="navbar-nav">
									<li className="nav-item mx-4 pt-2">
										<select>
											<option className="nav-link text-white" value="Bubble_Sort">Bubble Sort</option>
											<option className="nav-link text-white" value="Insertion_Sort">Insertion Sort</option>
											<option className="nav-link text-white" value="Quick_Sort">Quick Sort</option>
											<option className="nav-link text-white" value="Merge_Sort">Merge Sort</option>
										</select>
									</li>
									<li className="nav-item">
										<label>Input : </label>
										<button onClick={() => this.randomInput()}>Randomize Input</button>
									</li>
									<li className="nav-item">
										<button onClick={() => this.visualize()}>Visualize</button>
									</li>
							</ul>

					</div>
				</nav>

				<div className="boundary">
					<label>Selection Item Value : <label id='item_value'></label></label>
					<li className="nav-link">
						{this.state.lines}
					</li>
				</div>
			</div>
		);
	}

}


ReactDOM.render(
	<SortVisualizer />,
	document.getElementById('root')
);
